    this.codeAdapter = new CodeSourceAdapter('auto');
    this.databaseAdapter = new DatabaseAdapter();
    this.webAdapter = new WebAdapter();
    this.apiAdapter = new APIAdapter();
  }

interface SourceAdapterOptions {
  // Document chunking (for llamaindex adapter)
  chunk_size?: number;
  chunk_overlap?: number;
  chunking_strategy?: 'fixed_size' | 'semantic' | 'sentence' | 'paragraph';
  preserve_sentences?: boolean;

  // Other adapter-specific options
  [key: string]: any;
}

function createAPIAdapter(): APIAdapter {
  return new APIAdapter();
}

constructor(adapterName: 'typescript' | 'python' | 'html' | 'auto') {
    super();
    this.adapterName = adapterName;
    this.registry = this.initializeRegistry();
    this.uuidCache = new Map();
  }

function createWebAdapter(): WebAdapter {
  return new WebAdapter();
}

function createUniversalSourceAdapter(): UniversalSourceAdapter {
  return new UniversalSourceAdapter();
}

interface CodeSourceConfig extends SourceConfig {
  type: 'code';
  /**
   * @deprecated Use 'auto' - file types are now auto-detected regardless of this setting.
   * Kept for backward compatibility with existing configurations.
   */
  adapter: 'typescript' | 'python' | 'html' | 'auto';
  options?: {
    /** Export XML for debugging */
    exportXml?: boolean;
    /** XML export directory */
    xmlDir?: string;
    /** Parse comments/docstrings */
    parseComments?: boolean;
    /** Resolve imports */
    resolveImports?: boolean;
    /** Extract type definitions */
    extractTypes?: boolean;
  };
}

